import { CartActionType } from './cartactionType'

export const cartAddItem = (item) => ({
  type: CartActionType.CART_ADD_ITEM,
  payload: item,
})

export const cartRemoveItem = (item) => ({
  type: CartActionType.CART_REMOVE_ITEM,
  payload: item,
})
